WYMeditor.WymClassMozilla.prototype.keydown = function(evt) {

  //'this' is the doc
  var wym = WYMeditor.INSTANCES[this.title];
  var container = null;  
	var keycode = evt.which;
	if (keycode>=97)
		keycode -= 32;  // convert to upper case
  //alert(evt.type);
  if(evt.ctrlKey){
    if(keycode == 51){
      //CTRL+3 => H3
      evt.preventDefault();
      wym._exec(WYMeditor.FORMAT_BLOCK, WYMeditor.H3);
      return false;
   }
   if(keycode == 66){
      //CTRL+B => STRONG
      evt.preventDefault();
      wym._exec(WYMeditor.BOLD);
      return false;
    }
    if(keycode == 68){
      //CTRL+D => advanced class
      wym.toggleClassByName("advanced");
      return false;
    }
    if(keycode == 70){
      //CTRL+F => future class
      wym.toggleClassByName("future");
      return false;
    }
    if(keycode == 73){
      //CTRL+I => EMPHASIS
      wym._exec(WYMeditor.ITALIC);
      return false;
    }
    if(keycode == 80){
      //CTRL+P => Parshanim
      wym.toggleClassByName("mfrj");
      return false;
    }
    if(keycode == 81 || keycode==47){
      //CTRL+Q => Quote psuq
      wym.toggleClassByName("psuq");
      return false;
    }
    if(keycode == 83){
      //CTRL+S => Small
      wym.toggleClassByName("small");
      return false;
    }
    if(keycode == 85){
      //CTRL+U => Underline
      wym.toggleClassByName("u");
      return false;
    }
  }

  else if(evt.keyCode == 13) {
    if(!evt.shiftKey){
      //fix PRE bug #73
      container = wym.selected();
      if(container && container.tagName.toLowerCase() == WYMeditor.PRE) {
        evt.preventDefault();
        wym.insert('<p></p>');
      }
    }
  }
};


