WYMeditor.WymClassMozilla.prototype.keydown = function(evt) {

  //'this' is the doc
  var wym = WYMeditor.INSTANCES[this.title];
  var container = null;  
	var keycode = evt.which;
	if (keycode>=97)
		keycode -= 32;  // convert to upper case
  //alert(evt.type);
  if(evt.ctrlKey){
    if(keycode == 66){
      //CTRL+b => STRONG
			evt.preventDefault();
      wym._exec(WYMeditor.BOLD);
      return false;
    }
    if(keycode == 68){
      //CTRL+d => advanced class
      wym.toggleClassByName("advanced");
      return false;
    }
    if(keycode == 70){
      //CTRL+f => future class
      wym.toggleClassByName("future");
      return false;
    }
    if(keycode == 73){
      //CTRL+i => EMPHASIS
      wym._exec(WYMeditor.ITALIC);
      return false;
    }
    if(keycode == 80){
      //CTRL+p => Parshanim
      wym.toggleClassByName("mfrj");
      return false;
    }
    if(keycode == 81 || keycode==47){
      //CTRL+q => Quote psuq
      wym.toggleClassByName("psuq");
      return false;
    }
    if(keycode == 83){
      //CTRL+s => Small
      wym.toggleClassByName("small");
      return false;
    }
    if(keycode == 85){
      //CTRL+U => Underline
      wym.toggleClassByName("u");
      return false;
    }
  }

  else if(evt.keyCode == 13) {
    if(!evt.shiftKey){
      //fix PRE bug #73
      container = wym.selected();
      if(container && container.tagName.toLowerCase() == WYMeditor.PRE) {
        evt.preventDefault();
        wym.insert('<p></p>');
      }
    }
  }
};


WYMeditor.WymClassMozilla.prototype.keyup = function(evt) {

  //'this' is the doc
  var wym = WYMeditor.INSTANCES[this.title];
  
  wym._selected_image = null;
  var container = null;

  if(evt.keyCode == 13 && !evt.shiftKey) {
  
    //RETURN key
    //cleanup <br><br> between paragraphs
    jQuery(wym._doc.body).children(WYMeditor.BR).remove();
  }
  
  if(evt.keyCode != 8
       && evt.keyCode != 17
       && evt.keyCode != 46
       && evt.keyCode != 224
       && !evt.metaKey
       && !evt.ctrlKey) {
      
    //NOT BACKSPACE, NOT DELETE, NOT CTRL, NOT COMMAND
    //text nodes replaced by P
    
    container = wym.selected();
    var name = container.tagName.toLowerCase();

    //fix forbidden main containers
    if(
      name == "strong" ||
      name == "b" ||
      name == "em" ||
      name == "i" ||
      name == "sub" ||
      name == "sup" ||
      name == "a"

    ) name = container.parentNode.tagName.toLowerCase();

    if(name == WYMeditor.BODY) wym._exec(WYMeditor.FORMAT_BLOCK, WYMeditor.P);
  }
};

