var letters2 = ''.split('');
var letters3 = ''.split('');
function number2hebrew(num) {
	heb = "";
	while (num > 400) {
		heb += "";
		num -= 400;
	}
	if (num >= 100) {
		heb += letters3[ Math.floor(num / 100) - 1 ];
		num %= 100;
	}
	if (num >= 10) {
		if (num == 15) {
			heb += "";
			num = 0;
		}
		else if (num == 16) {
			heb += "";
			num = 0;
		}
		else {
			heb += letters2[ Math.floor(num / 10) - 1 ];
			num %= 10;
		}
	}
	if (num >= 1) {
		heb += letters1[ num - 1 ];
	}
	
	return heb;
}




