function text2HTML(theText) {
	theText = theText.
		replace(/(http:\/\/[^ \r\n\t()<>{}]+)/gi, "<a href='$1'>$1</a>").
		replace(/\cM/g, "").
		replace(/\n\n/g, "</p><p>").
		replace(/\n/g, "<br />").
		replace(/&lt;/gi, "<").
		replace(/&gt;/gi, ">").
		replace(/&quot;/gi, "\"").
		replace(/^(<p>)+/,"").
		replace(/(<\/p>)+$/,"");
	if (theText.length>0)
		return "<p>" + theText + "</p>";
	else
		return "";
}


