UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>- כאשר <strong>יעשו הרע</strong>, <strong>ישמחו</strong>, ולא ידאגו כדרך המאמין בהשגחה ושכר ועונש, שאף אם בא לידו דבר עבירה בהסתת היצר הרע, מלא הוא מדאגה מאימת הדין;&#160;&#160;&#160; ו<strong>יגילו</strong> <strong>ב</strong>עת ימצאו מקום <strong>להפוך</strong> הכוונה אחר<strong> רוע</strong> מחשבתם.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 2 AND `verse_number` = 14 AND 1 LIMIT 1;

