UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>יפיח אמונה</strong> (המורגל לדבר דבריו באמונה), כל-שכן בעדותו <strong>יגיד צדק</strong>;&#160;&#160;&#160; אבל המורגל לדבר <strong>שקרים</strong>, עם כי המה בדברים של מה בכך, עם-כל-זה קרוב הדבר אשר גם בדבר <strong>העדות - מרמה</strong> בפיו, והרבה יש לחקור בדבריו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 17 AND 1 LIMIT 1;

