UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>בלב</strong> ה<strong>חורש </strong>(חושב)<strong> רע</strong> תמצא <strong>מרמה</strong>, והוא מיצר ודואג שלא יתגלה;&#160;&#160; אבל <strong>היועצים</strong> על ה<strong>שלום</strong> עוד <strong>ישמחו</strong> אם יתגלה.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 20 AND 1 LIMIT 1;

