UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>אדם ערום כוסה דעת</strong> - אף דברי חכמה לא יגלה כי אם להראוים לה;&#160;&#160;&#160; <strong>ו</strong>מי שלבו <strong>לב כסילים</strong> - <strong>יקרא</strong> ויפרסם אף דברי <strong>איוולת</strong>.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 23 AND 1 LIMIT 1;

