UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה<strong>אוהב</strong> את ה<strong>מוסר</strong> הוא <strong>אוהב דעת</strong>, כי בשמעו את המוסר יקנה דעת מה שלא שמע מאז;&#160;&#160; אבל ה<strong>שונא תוכחת</strong> הוא <strong>בער</strong> ושוטה ונמשל כבהמות, כי ישאר באולתו.</p>\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 1 AND 1 LIMIT 1;

