UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>ב</strong>עבור<strong> פשע שפתים</strong> המדבר סרה יבוא עליו <strong>מוקש רע</strong>;&#160;&#160; אבל <strong>הצדיק</strong> בנועם אמריו <strong>יצא מהצרה</strong> הבאה.\r\n\r\n</p>', `tirgum` = '<p><strong>כשהשפתיים פושעות</strong> (מורדות) בבעליהן ומוציאות פליטת-פה לא מכוונת, זהו \r\n<strong>מוקש</strong>  (מכשול) \r\n<strong>רע</strong>  ומסוכן;&#160;&#160; אולם \r\n<strong>הצדיק</strong>  נזהר בדיבורו, וגם אם נכשל - יודע להתנצל ו<strong>לצאת מצרה</strong>  זו.</p>', `main_qod_subjects` = NULL, `hqblot` = '<p>פסוק נוסף מדבר על שפתי הצדיק, \r\n<a class=\"psuq\" href=\"/tnk1/prqim/t2810.htm#32\">משלי י32</a>: \"<q class=\"psuq\"><strong>שִׂפְתֵי צַדִּיק</strong> יֵדְעוּן רָצוֹן</q>\"\r\n (<a href=\"http://localhost/tnk1/ktuv/mj/10-32.html\">פירוט</a>) - שם הכוונה היא, ששפתי הצדיק כביכול יודעות מה הוא רוצה \r\nלהגיד, כי כל מחשבותיו של הצדיק הן בצדק וביושר, אין לו מה להסתיר, ולכן \r\nאינו מתבלבל ואינו נכשל בפליטת פה. &#160; הפסוק שלנו מלמד, שגם במקרים הנדירים שבהם הצדיק בכל-זאת נכשל בפליטת פה, הוא יודע לצאת מזה בכבוד.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 13 AND 1 LIMIT 1;

