UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>אויל</strong> בו <strong>ביום</strong> שכועס על מי, ביום הזה <strong>יודע כעסו</strong> ויתפרסם בו בעולם, כי מיד יתקוטט עמו ויבזהו;&#160;&#160; אבל ה<strong>ערום</strong>, עם כי יכעס, <strong>כוסה</strong> הוא את ה<strong>קלון</strong>, ולא יבזה את רעהו מיד בפרסום.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 16 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>יפיח אמונה</strong> (המורגל לדבר דבריו באמונה), כל-שכן בעדותו <strong>יגיד צדק</strong>;&#160;&#160;&#160; אבל המורגל לדבר <strong>שקרים</strong>, עם כי המה בדברים של מה בכך, עם-כל-זה קרוב הדבר אשר גם בדבר <strong>העדות - מרמה</strong> בפיו, והרבה יש לחקור בדבריו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 17 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>יש בוטה</strong> - <strong>יש</strong> מי <strong>יבטא</strong> בשפתיו ויזיק בזה כאלו <strong>מדקיר בחרב</strong>, והוא המדבר לשון הרע ומוציא שם רע;&#160;&#160;&#160; אבל אמרי <strong>לשון חכמים</strong> המה עוד ל<strong>מרפא</strong>, כי במתק אמרי נועם יסית האדם לתשובה ושב ורפא לו.</p>', `tirgum` = '<p><strong>יֵשׁ</strong> אדם \r\nה<strong>מבטא</strong>  בשפתיו שבועה או נדר הגורמים נזק רב לעצמו ולזולתו כמו \r\n<strong>דקירות</strong>  של \r\n<strong>חרב</strong>;&#160;&#160; אולם אנשים <strong>חכמים</strong> יודעים \r\n<strong>לרפא</strong>  את המצב \r\n<strong>בלשונם</strong> בכך שהם מפרשים את השבועה והנדר באופן המונע את הנזק.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 18 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>שפת אמת </strong>מתקיימת <strong>עד</strong> עולם,&#160;&#160; אבל <strong>לשון שקר</strong> לא תתקיים כי-אם <strong>עד רגע</strong>, הוא עת האמירה, כי לאחר-זה יתברר השקר.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 19 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>בלב</strong> ה<strong>חורש </strong>(חושב)<strong> רע</strong> תמצא <strong>מרמה</strong>, והוא מיצר ודואג שלא יתגלה;&#160;&#160; אבל <strong>היועצים</strong> על ה<strong>שלום</strong> עוד <strong>ישמחו</strong> אם יתגלה.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 12 AND `verse_number` = 20 AND 1 LIMIT 1;

