UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>אם נראה <strong>חכמה</strong> באדם, בוודאי שמע <strong>מוסר האב</strong> ולזה נתחכם; &#160; ואם נראה שהוא <strong>לץ</strong>, בוודאי <strong>לא קבל גערת</strong> האב שהוכיחו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 1 AND 1 LIMIT 1;

