UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה<strong>צדקה</strong> עצמה תלמד זכות ו<strong>תצור</strong> את ה<strong>תם דרך</strong>, זה הנותן צדקה בתמימות לא להתיהר;&#160;&#160;&#160; <strong>ו</strong>ה<strong>רשעה</strong> עצמה תוליך את בעל <strong>החטאת</strong> בדרך <strong>מסולפת</strong> (מעוקלת ומעוקמת) להכשל בה.\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 6 AND 1 LIMIT 1;

