UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>לפעמים יִמָּצֵא המתעסק <strong>להתעשר</strong> <strong>ואין</strong> לו <strong>כל</strong> מאומה, והמתעסק <strong>להתרושש </strong>(להעני את עצמו) ישיג בזה <strong>הון רב</strong> - כי המרבה הון בגזל יעני, והממעיט ההון בצדקה יעשיר.</p>', `tirgum` = '<p>\r\n<strong>יש</strong>  אדם \r\n<strong>שמתחזה לעשיר</strong>  אך בעצם \r\n<strong>אין</strong>  לו \r\n<strong>כלום</strong>;&#160;&#160; ויש אדם \r\n<strong>שמתחזה לרש</strong>  (עני) אך בעצם יש לו \r\n<strong>הון רב</strong>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 7 AND 1 LIMIT 1;

