UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הון</strong> הבא <strong>מהבל</strong>, ר\"ל לא מיגיע כפים כי-אם מגזל וחמס, ההון ההוא <strong>ימעט</strong> בכל עת ולא תתקיים;&#160;&#160;&#160; אבל ה<strong>קובץ</strong> הון <strong>עם</strong> מלאכת ה<strong>יד</strong>, ההון ההוא <strong>יתרבה</strong>.&#160;&#160; <small class=\"small\">(<strong>על יד</strong> - ביד, וכן <strong>עמדתם על חרבכם</strong> ומשפטו בחרבכם)</small>.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\"></span></h3>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 11 AND 1 LIMIT 1;

