SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>כל ערום</strong> - <strong>מעשה</strong> ה<strong>ערום</strong> המה <strong>בדעת</strong> וכוונה מיוחדת, ואם כי לא יבינו כל; אבל ה<strong>כסיל פורש אולתו</strong> למען יכירו הכל כי אולת הוא.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>יפרוש</strong> - מלשון פרישה ושטיחה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 16 AND 1 LIMIT 1;

