SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>מלאך רשע</strong> - שליח <strong>רשע</strong>, המשנה מדברי משלחו לרעה, הוא בעצמו <strong>יפול בהרעה</strong> ההיא; אבל <strong>ציר</strong> מאנשי <strong>אמונים ירפא</strong> עוד את דבר השליחות, אם יראה בדבר חולשה מצד מה ירפאנה במתק הלשון.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מלאך</strong> - ענין שליח, כמו ():</span> <strong>מלאך שלוח אליהם</strong>.</p>\r\n<p><strong>וציר</strong> - גם הוא ענין שליח, כמו ():</span> <strong>וציר בגוים שולח</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 17 AND 1 LIMIT 1;

