SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>ריש</strong> - עניות ובזיון מעותדים לבוא על המבטל <strong>המוסר</strong>; אבל <strong>השומר</strong> בלבו דברי <strong>התוכחות</strong>, <strong>יכובד</strong> מן הבריות.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>פורע</strong> - ענין ביטול.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>ה<strong>פורע</strong> (מתייחס בביטול ל-) דברי <strong>מוסר</strong> (ביקורת) שמותחים עליו - יקבל <strong>ריש</strong> (עוני) <strong>וקלון</strong> (ביזיון);&#160;&#160;&#160; אולם ה<strong>שומר</strong> בליבו ומתייחס ברצינות לדברי <strong>תוכחת</strong> (ביקורת) שמותחים עליו - יזכה <strong>לכבוד</strong>.</p>', `mqorot` = '<p>\r\n<a href=\"http://localhost/tnk1/kma/qjrim1/musr_tokxa.html\">\r\nמוסר \r\nותוכחת  הם מושגים המציינים ביקורת</a>; האמנם צריך לקבל כל עצה ודבר-ביקורת שאומרים לנו, לא משנה מה הנימוקים שלו?\r\n  \r\n</p><p>נראה לי שלא:\r\nבלשון המקרא, הפועל \r\n\r\n<a href=\"http://localhost/tnk1/kma/qjrim1/pra.html\">פרע</a>אין משמעו \"לא קיבל\", אלא \"פרק, פרץ, שיחרר לגמרי\".</p><p>לפי זה, \r\n<strong>פורע מוסר</strong>    הוא אדם שמשחרר לגמרי את דברי הביקורת שאומרים\r\nלו - הוא אינו מתייחס אליהם כלל, לא חושב עליהם ולא נותן להם כל ערך - הוא\r\n\"זורק אותם לכל הרוחות\". אדם כזה עלול להפסיד דברי-ביקורת מועילים, ולעשות\r\nמעשים שיצטער עליהם.\r\n  \r\n</p>  \r\n<p>לא חייבים לקבל את כל העצות שנותנים לנו, אך צריך להתייחס לכולן ברצינות ובשיקול-דעת, ולא \r\n<strong>לפרוע</strong>    אותן.\r\n  \r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 18 AND 1 LIMIT 1;

