UPDATE `sgulot` SET `tirgum` = '<p>- וגם כאשר <strong>תקנה</strong> <strong>חכמה ובינה</strong> ממקורות אחרים, <strong>אל תשכח</strong> את הערכים והמצוות שלמדת בבית, <strong>ואל תטה</strong> מהדברים <strong>שאמרתי</strong> ולימדתי אותך -</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 5 AND 1 LIMIT 1;

