UPDATE `sgulot` SET `hqblot` = '<p class=\"future\"><span class=\"future\">הביטוי שנות חיים נזכר  בפסוקים נוספים בספר: </span></p><p class=\"future\"><a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2803.htm#2\">משלי ג2</a>: \"<q class=\"psuq\">כִּי אֹרֶךְ יָמִים <strong>וּשְׁנוֹת חַיִּים</strong> וְשָׁלוֹם <strong>יוֹסִיפוּ</strong> לָךְ</q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mj/03-02.html\">פירוט</a>).</p><p class=\"future\"><a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2804.htm#10\">משלי ד10</a>: \"<q class=\"psuq\">שְׁמַע בְּנִי וְקַח אֲמָרָי, <strong>וְיִרְבּוּ</strong> לְךָ <strong>שְׁנוֹת חַיִּים</strong></q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mj/04-10.html\">פירוט</a>).</p><p class=\"future\"><a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2809.htm#11\">משלי ט11</a>: \"<q class=\"psuq\">כִּי בִי <strong>יִרְבּוּ</strong> יָמֶיךָ, וְיוֹסִיפוּ לְּךָ <strong>שְׁנוֹת חַיִּים</strong></q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mj/09-11.html\">פירוט</a>).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 10 AND 1 LIMIT 1;

