UPDATE `sgulot` SET `mcudot` = '<p><strong>פרע</strong> (בטל והשבת) את הדרך <strong>ההוא</strong>, <strong>ואל תעבור בו</strong>; &#160;&#160; <strong>שטה</strong> (נטה וסור) <strong>מעליו</strong> <strong>ועבור</strong> ממנו.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 15 AND 1 LIMIT 1;

