UPDATE `sgulot` SET `mcudot` = '<p><strong>ו</strong>ה<strong>אורח</strong> שה<strong>צדיקים</strong> הולכים בו הוא <strong>כאור היום</strong>, המזהיר <strong>והולך ומאיר</strong> בכל פעם יותר <strong>עד</strong> אשר <strong>יכון היום</strong> בשלימותו, והוא חצות היום; רוצה לומר, בכל עת ישכילו יותר לעמוד על דעת התורה.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 4 AND `verse_number` = 18 AND 1 LIMIT 1;

