UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>הנבהל</strong>   (חרד) שמא יאבד \r\n<strong>הונו</strong>   נעשה \r\n<strong>איש רע עין</strong>, \r\n<strong>רע</strong>   לו לראות \r\n<strong>בעיניו</strong>   שאנשים אחרים נהנים מרכושו ולכן אינו משתף פעולה עם הזולת;&#160;&#160; אבל משום כך גם אין לו עם מי להתייעץ, הוא \r\n<strong>לא</strong>   יוכל \r\n<strong>לדעת</strong>   לבדו את כל המידע הדרוש כדי להשקיע בתבונה, ו<strong>יבוא</strong>   עליו \r\n<strong>מחסור</strong>.</p>', `tosfot` = NULL, `kotrt` = 'השומר על רכושו מפני שותפים, יאבד אותו כי לא יהיו לו יועצים' WHERE `book` = 'משל' AND `chapter_number` = 28 AND `verse_number` = 22 AND 1 LIMIT 1;

