UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>כמו <strong>מעבר</strong> של <strong>סופה</strong>, החולפת מעל הראש ונעלמת במהירות, כך גם <strong>הרשע</strong>, מתגאה ועולה לגדולה אבל גם חולף ונעלם במהירות;&#160; &#160; וכמו <strong>יסוד</strong>, שהוא נמוך אבל מחובר לאדמה וקיים <strong>לעולם</strong>, כך גם <strong>הצדיק</strong>, עניו ושפל-רוח אבל יציב ומתקיים לאורך זמן.</p><p>/ <strong>כאשר עוברת סופה</strong>, אסון מהיר ופתאומי, העם <strong>הרשע</strong> אינו מצליח לשמור על אחדות, מתפזר לכל עבר <strong>ואיננו</strong>;&#160;&#160;&#160; אולם העם <strong>הצדיק</strong> מצליח לשמור על שיתוף פעולה ועזרה הדדית גם במהלך הסופה, הצדיקות שלו היא <strong>יסוד</strong> אשר מחזיק מעמד <strong>לעולם</strong>.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL, `kotrt` = 'בעת סערה, הרשע מתפרק והצדיק מתחזק' WHERE `book` = 'משל' AND `chapter_number` = 10 AND `verse_number` = 25 AND 1 LIMIT 1;

