UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>כשהצדיקים</strong>  \r\n<strong>מתרבים</strong>  ועולים למעמד \r\n<strong>רב</strong>  ונכבד, <strong>העם שמח</strong>;&#160;&#160; אולם \r\n<strong>כשהרשע</strong>  מגיע \r\n<strong>לממשלה</strong>, כל <strong>עם נאנח</strong>  מצער.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 29 AND `verse_number` = 2 AND 1 LIMIT 1;

