UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>שופט \r\n<strong>צדיק</strong>  בודק באופן אישי \r\n<strong>ויודע</strong>  מקרוב את כל פרטי \r\n<strong>הדין</strong>   של אנשים \r\n<strong>דלים</strong>, שאין להם כסף לייצוג משפטי;&#160;&#160;&#160; אולם שופט \r\n<strong>רשע</strong>   \r\n<strong>לא</strong>   \r\n<strong>מתבונן</strong>   כדי \r\n<strong>לדעת</strong>  מקרוב את פרטי הדין, אלא מסתמך על העובדות השטחיות.</p>', `tosfot` = NULL, `kotrt` = 'השופט הצדיק בודק את העובדות בעצמו ולא מסתמך על עורכי דין' WHERE `book` = 'משל' AND `chapter_number` = 29 AND `verse_number` = 7 AND 1 LIMIT 1;

