UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>כשהרשעים מתרבים</strong>   ועולים לשלטון, \r\n<strong>מתרבים</strong>   מעשי \r\n<strong>הפשע</strong>, המרד והבגידה בינם לבין עצמם, \r\n<strong>והצדיקים</strong>   מסתכלים מהצד \r\n<strong>ורואים</strong>   את \r\n<strong>המפלה</strong>   שהרשעים מביאים על עצמם.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 29 AND `verse_number` = 16 AND 1 LIMIT 1;

