UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אם אדם \r\n<strong>מפנק</strong>  את \r\n<strong>עבדו</strong>  כאשר הוא \r\n<strong>נער</strong> - אז&#160;<strong>באחריתו</strong>, כשיתבגר, <strong>יהיה</strong>  \r\nהעבד <strong>מנוון</strong>  ועצל.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 29 AND `verse_number` = 21 AND 1 LIMIT 1;

