UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>מוקש</strong>  (מכשול נסתר) \r\n<strong>נותן</strong> \r\n<strong>חרדה</strong>  (בהלה פתאומית) בלב \r\n<strong>האדם</strong>;&#160;&#160; <strong>\r\nוהבוטח  בה\'</strong> מאמין שה\' נתן בליבו את החרדה כדי להזהיר אותו, נזהר ו<strong>נשגב</strong> (ניצל) מהמוקש.</p>', `hqblot` = '<p>בועז הרגיש \r\n<strong>חרדה</strong>  באמצע הלילה, \r\n<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2903.htm#8\">רות ג8</a>: \"<q class=\"psuq\">וַיְהִי בַּחֲצִי הַלַּיְלָה, \r\n<strong>וַיֶּחֱרַד</strong> הָאִישׁ וַיִּלָּפֵת, וְהִנֵּה אִשָּׁה שֹׁכֶבֶת מַרְגְּלֹתָיו</q>\". החרדה הזאת נבעה \r\n<strong>ממוקש</strong>  רוחני, \"<q class=\"mfrj\">כי הסטרא-אחרא היתה חושבת להחטיאו במוקש הזה של אישה המזומנת לפניו</q>\", אולם, \"<q class=\"mfrj\">בועז\r\n הצדיק היה <strong>בוטח בה\'</strong> יתברך בכל ענייניו שלא תבוא תקלה על ידו, ולכן נעזר <strong>\r\nונשגב</strong> ממנו ית\', שניתן בליבו לקפוץ בשבועה כדי להכניע את יצרו, ועל-ידי-כך \r\nניצול מן החטא, וזכה ליהנות בהיתר ממה שלא רצה ליהנות באיסור</q>\"\r\n<small class=\"small\"> (רמ\"ד ואלי)</small>;&#160;&#160;&#160; ומכאן גם באופן כללי, \"<q class=\"mfrj\"><strong>מוקש</strong>  עבירה \r\n<strong>יתן</strong>  \r\n<strong>חרדה</strong>  לאדם</q>\" \r\n<small>(רש\"י).</small></p><p class=\"future\">רעיון דומה ב<a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t2818.htm#10\">משלי יח10</a>: \"<q class=\"psuq\">מִגְדַּל עֹז שֵׁם <strong>ה\'</strong>, בּוֹ יָרוּץ צַדִּיק <strong>וְנִשְׂגָּב</strong></q>\" (<a href=\"http://tora.us.fm/tnk1/ktuv/mjly/mj-18-10.html\">פירוט</a>).</p>', `tosfot` = NULL, `kotrt` = 'חרדה פתאומית היא אזהרה מפני מוקש' WHERE `book` = 'משל' AND `chapter_number` = 29 AND `verse_number` = 25 AND 1 LIMIT 1;

