UPDATE `sgulot` SET `prefix` = NULL, `hqblot` = '<p class=\"future\"><strong>דעת קדושים</strong>    נזכרה גם ב<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2809.htm#10\">משלי ט10</a>: \"<q class=\"psuq\">תְּחִלַּת חָכְמָה יִרְאַת ה\', \r\n<strong>וְדַעַת קְדֹשִׁים</strong> בִּינָה</q>\" (<a href=\"http://localhost/tnk1/ktuv/mj/09-10.html\">פירוט</a>).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 30 AND `verse_number` = 3 AND 1 LIMIT 1;

