UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>הרחק ממני שבועת \r\n<strong>שווא</strong>    והרחק ממני הבטחות \r\n<strong>כזב</strong>; &#160; \r\n<strong>אל תתן לי</strong>     \r\n<strong>רֵש</strong>     (עוני) ואל תתן לי \r\n<strong>עושר</strong>; תן לי את \r\n<strong>לחם חוקי</strong>,  הכמות  המדוייקת שאני צריך כדי להתקיים.</p>', `tosfot` = NULL, `kotrt` = 'לא רוצה עוני ולא רוצה עושר' WHERE `book` = 'משל' AND `chapter_number` = 30 AND `verse_number` = 8 AND 1 LIMIT 1;

