UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- <strong>וכן</strong> אני לא יודע לזהות את \r\n<strong>דרך האישה המנאפת</strong>  - אצל כמה גברים היא עברה, שהרי בכל פעם היא \r\n<strong>אוכלת</strong>    (נהנית) ואז \r\n<strong>מוחה את פיה</strong>    (מתנקה) ואומרת: \r\n<strong>\"לא פעלתי אוון</strong>    (רע)!\".</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 30 AND `verse_number` = 20 AND 1 LIMIT 1;

