UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- בגלל \r\n<strong>עבד</strong>    אשר נעשה \r\n<strong>מלך</strong>    בבת אחת,</p><p>ובגלל \r\n<strong>נבל</strong>    (אדם גס וחייתי) אשר נעשה בבת-אחת עשיר \r\n<strong>ושבע</strong>;</p>', `tosfot` = NULL, `kotrt` = 'עבד העולה בבת-אחת לשלטון מזעזע את הארץ' WHERE `book` = 'משל' AND `chapter_number` = 30 AND `verse_number` = 22 AND 1 LIMIT 1;

