UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>הנמלים</strong>     הם \r\n<strong>עם</strong>  קטן \r\n<strong>ולא עז</strong>, ולמרות זאת הן יודעות לנהל תקציב מאוזן, <strong>מכינות  לחם בקיץ</strong> וחוסכות את העודף לחורף.</p> \r\n<p>/ \r\n<strong>הנמלים</strong>     הם \r\n<strong>עם</strong>  חלש \r\n<strong>ולא עז</strong>, וכדי להתמודד עם חולשתן הן מנצלות היטב את הזמן ו<strong>מכינות בקיץ לחם</strong>  לכל השנה.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL, `kotrt` = NULL WHERE `book` = 'משל' AND `chapter_number` = 30 AND `verse_number` = 25 AND 1 LIMIT 1;

