UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>השפנים</strong>  הם \r\n<strong>עם</strong>  קטן \r\n<strong>ולא עצום</strong>, ולמרות זאת הם יודעים \r\n<strong>לשים</strong>  את \r\n<strong>ביתם</strong>  \r\n<strong>בסלע</strong>, לבנות בית בכל תנאי-שטח.</p> \r\n<p>/ \r\n<strong>השפנים</strong>    הם \r\n<strong>עם</strong>    חלש \r\n<strong>ולא עצום</strong>, וכדי להתמודד עם חולשתם  הם מנצלים היטב את תנאי השטח ומתגוררים בתוך מחילות מוגנות \r\n<strong>בסלעים</strong>.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL, `kotrt` = 'ללמוד מהשפנים לבנות בתנאי שטח' WHERE `book` = 'משל' AND `chapter_number` = 30 AND `verse_number` = 26 AND 1 LIMIT 1;

