UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>אם נבלת</strong>, נשחקת והתעייפת בתפקידך \r\n<strong>כנשיא</strong>   ומנהיג הציבור, ואם \r\n<strong>זמות</strong>, שקעת במחשבות עמוקות מרוב עייפות וחוסר-ריכוז - שים \r\n<strong>יד לפה</strong>, קח הפסקה מדיבורים ונאומים, אכול דבר-מה, וחזור לתפקידך רענן -</p> \r\n<p>/ אם \r\n<strong>נבלת</strong>, הושפלת ע\"י אדם שניבל את פיו כלפיך, \r\n<strong>בה</strong>  - באותה השפלה - \r\n<strong>תינשא</strong>  ותראה את גדולתך;&#160;&#160; \r\n<strong>ואם זמות</strong>  - התמלאת במחשבות כעס ונקם - שים \r\n<strong>יד לפה</strong>  ואל תגיב להשפלה -</p>', `tosfot` = NULL, `kotrt` = 'הנהג בערנות - עצור להתרעננות' WHERE `book` = 'משל' AND `chapter_number` = 30 AND `verse_number` = 32 AND 1 LIMIT 1;

