SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>העני \r\n<strong>ישתה</strong>   יין \r\n<strong>וישכח</strong>   את \r\n<strong>רישו</strong>   (עוניו); &#160; הנדון למוות ישתה שֵכָר \r\n<strong>ולא יזכור עוד</strong>   את \r\n<strong>עמלו</strong>   (רשעו שבגללו נדון למוות).</p>', `tosfot` = NULL, `kotrt` = 'תנו לעני לשכוח את עוניו' WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 7 AND 1 LIMIT 1;

