UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>אתה, השופט, \r\n<strong>פתח פיך</strong>   כדי לעזור לזה ש<strong>נאלם</strong> (משתתק) בזמן המשפט מרוב התרגשות;&#160;&#160; פתח פיך \r\n<strong>בכל</strong>   פעם שאתה עושה \r\n<strong>דין</strong>   עם אנשים \r\n<strong>החולפים</strong>   ועוברים בבית המשפט, אינם נמצאים בו באופן קבוע ומשום כך מתקשים להתבטא;</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 8 AND 1 LIMIT 1;

