UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>היא \r\n<strong>גומלת</strong>  לו \r\n<strong>טוב</strong>  על מעשיו הטובים, אבל \r\n<strong>לא</strong>  גומלת לו \r\n<strong>רע</strong>  על מעשיו הרעים; &#160; כך היא נוהגת בעקביות \r\n<strong>כל ימי חייה</strong>  עד שבעלה יכול לבטוח בה בכל ליבו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 12 AND 1 LIMIT 1;

