UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>כשנגמרים לה חומרי-הגלם לעבודה, היא \r\n<strong>דורשת</strong>  (מחפשת) \r\n<strong>צמר ופשתים</strong>, \r\n<strong>ועושה בחפץ</strong>  (ברצון ובשמחה) מלאכת \r\n<strong>כפיים</strong>.</p>', `tosfot` = NULL, `kotrt` = 'אשת חיל חרוצה בלי תירוצים' WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 13 AND 1 LIMIT 1;

