SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `ecot` = '<p>\r\nהפסוק ממליץ לחשוב, תוך כדי העבודה, איפה  להשקיע את הכסף שמרוויחים בעבודה, ובפרט - להשקיע אותם בקרקע.</p>    \r\n<p>כך עושה אשת חיל:\r\nהיא עובדת בטקסטיל (פסוק 13: \"<q class=\"psuq\">דרשה צמר ופשתים...</q>\"), אבל תוך כדי עבודה היא מתכננת להשקיע את הכסף שתרויח. לכן היא \r\n<strong>לקחה</strong>    שדה, ואז \r\n<strong>מפרי כפיה</strong>       = מהשכר שקיבלה כתוצאה מעבודת-הכפיים בטוויה - שתלה בו כרם.</p> \r\n<p>\"<q class=\"mfrj\">והנה כבר ידוע, \r\n<strong>שאדם שאין לו קרקע אינו אדם</strong>, כי הוא העיקר לעשות את לחמו ולסמוך \r\nעליו באסיפת תבואותיו מדי שנה בשנה. ולכן אשת חיל... כל מגמתה לקנות שדה \r\nבמעותיה ולטעת כרם מפרי כפיה, ולא לבזבז את שלה בהבלי נשים... בקישוטים \r\nובתכשיטים</q>\"\r\n<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `tosfot` = NULL, `kotrt` = 'אשת חיל משקיעה את רווחיה בקרקע' WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 16 AND 1 LIMIT 1;

