UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p><strong>כמו אוניות מסחר</strong>, היא עוסקת בייבוא וייצוא, ומוכנה לנסוע גם למקומות \r\n<strong>רחוקים</strong>    כדי \r\n<strong>להביא</strong>    משם את \r\n<strong>לחמה</strong>    (מזונה ופרנסתה).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 14 AND 1 LIMIT 1;

