UPDATE `sgulot` SET `prefix` = NULL, `main_qod_subjects` = NULL, `hqblot` = '<p>פסוק אחד (פסוק 14) אומר שאשת חיל עוסקת בייבוא - קונה את לחמה מארץ רחוקה (<a href=\"http://tora.us.fm/tnk1/ktuv/mjly/mj-31-14.html\">פירוט</a>). </p><p>פסוק אחד (פסוק 24) אומר שאשת חיל עוסקת בייצוא - מוכרת את תוצרתה לסוחר נכרי.</p><p>מכאן, שאשת חיל שומרת על מאזן-תשלומים מאוזן...</p>', `tosfot` = NULL, `kotrt` = 'אשת חיל עוסקת בייצוא' WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 24 AND 1 LIMIT 1;

