UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>בניה קמים</strong>  (עומדים לכבודה) \r\n<strong>ומאשרים</strong>  אותה (משבחים את דרכה);&#160;&#160; גם \r\n<strong>בעלה</strong>  קם לכבודה \r\n<strong>ומהלל</strong>  אותה:</p>', `main_qod_subjects` = NULL, `tosfot` = NULL, `kotrt` = 'הבנים והאב קמים לכבוד האם' WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 28 AND 1 LIMIT 1;

