UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>\r\n<strong>תנו לה</strong> ליהנות \r\n<strong>מהפרי</strong>   (התוצאה) של מעשי \r\n<strong>ידיה</strong>;&#160;&#160; פרסמו את \r\n<strong>מעשיה</strong>   הטובים \r\n<strong>בשערי</strong>   העיר כדי שיביאו לה שבח \r\n<strong>ותהילה</strong>   כראוי לה.</p><p>/ <strong>תנו לה</strong> דברי שיר ושבח על <strong>פרי</strong> מעשי <strong>ידיה</strong>; <strong>ויהללו</strong> אותה <strong>בשערים</strong> בעלה ובניה, כי הם כולם <strong>מעשיה</strong> - היא עשתה אותם למה שהם.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 31 AND `verse_number` = 31 AND 1 LIMIT 1;

