UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- והוא אסף וכתב את המשלים הללו כדי <strong>שיידע</strong> ויזכור את דברי <strong>החכמה&#160;והמוסר</strong> שלמד מאחרים,&#160;&#160;&#160; וכדי <strong>שיבין</strong> (יסיק מסקנות חדשות) ויוכל לומר <strong>אמרי בינה</strong> לאחרים;</p>', `mqorot` = '<p>האות <strong>ל</strong> מציינת את התכלית של מה שלפניה: <strong>משלי שלמה... לדעת חכמה</strong> - המשלים שכתב שלמה כדי שיידע חכמה. ולא כתב להודיע אלא <strong>לדעת</strong> - קודם-כל שיידע הכותב בעצמו, ורק אחר-כך יוכל ללמד אחרים&#160;<small>(וכן כתב גם הרמח\"ל בהקדמה לספרו \"מסילת ישרים\", שתכליתו קודם-כל \"ללמד לעצמי\" ואחר-כך גם \"להזכיר לאחרים\")</small>.</p><h3>מה ההבדל בין <strong>חכמה</strong> לבין <strong>מוסר</strong>?</h3><p>1. \r\n<strong>חכמה</strong>   היא ידיעת החוק, \r\n<strong>מוסר</strong>   הוא ידיעת השכר על קיום החוק והעונש על הפרת החוק; &#160;&#160;\r\n<strong>חכמה</strong>   היא בשכל, \r\n<strong>מוסר</strong>   משפיע על הרצון <small>(מלבי\"ם על הפסוק; חפץ חיים בפתיחה לספר שמירת הלשון). </small></p>\r\n  \r\n\r\n     \r\n \r\n<p>2. \r\n<strong>חכמה</strong>   היא להימנע מצרות, \r\n<strong>ומוסר</strong>   הוא לצאת מצרות; \r\n<strong>חכמה</strong>   היא לכתחילה, \r\n<strong>מוסר</strong>   הוא בדיעבד \r\n<small>(הגר\"א על הפסוק)</small>.\r\n</p>\r\n    \r\n  \r\n<p>3. \r\n<strong>חכמה</strong>   היא להוסיף תכונות טובות, \r\n<strong>מוסר</strong>   הוא להסיר תכונות רעות&#160;<small>(אורי בירן, \"נקודת חיבור\")</small>.</p>\r\n<p>4. ולענ\"ד <strong>חכמה</strong>     \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/xkm.html\">= לימוד על-ידי שמיעה והקשבה</a>, לימוד חיובי - מה לעשות;\r\n<strong>&#160;&#160; מוסר</strong>     \r\n<a href=\"http://localhost/tnk1/kma/qjrim1/musr.html\">= לימוד על-ידי ביקורת וייסורים</a>, לימוד שלילי - מה לא לעשות.&#160;&#160; ואכן שני סוגי הלימוד נמצאים בספר משלי אפילו בנושא אחד. כך למשל, חינוך לחריצות נמצא במשלי ו6-11 מצד ה<strong>חכמה</strong> ע\"י דוגמה חיובית - הנמלה;&#160; ובמשלי כד30-34 מצד ה<strong>מוסר</strong> ע\"י דוגמה שלילית - העצל ששדהו נהרס.</p><h3>ומה ההבדל בין <strong>לדעת</strong> לבין <strong>להבין</strong>?</h3><p><strong>בינה </strong><a href=\"http://localhost/tnk1/kma/qjrim1/bwn.html\">= ניתוח, הגיון, הסקת מסקנות</a>.</p><p>הצלע הראשונה של הפסוק, <strong>דעת חכמה ומוסר</strong>, מייצגת את השלב הראשון בחינוך, שבו האדם למד ממקורות חיצוניים ומקבל ידע מאחרים;</p><p>הצלע השניה של הפסוק, <strong>להבין אמרי בינה</strong>, מייצגת את השלב השני, שבו האדם לומד ממקורות פנימיים, מסיק מסקנות, <strong>מבין</strong> דבר מתוך דבר ו<strong>בונה</strong> עוד קומה של ידע.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 2 AND 1 LIMIT 1;

