UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>- כי יש בספר רבדים רבים: הקורא יכול <strong>להבין</strong>  את המשמעות המילולית הפשוטה של כל <strong>משל</strong>, אך אם יתעמק יותר יבין גם את ה<strong>מליצה</strong> (הנמשל);&#160;&#160; הוא יוכל למצוא כאן&#160;<strong>דברי חכמים</strong> פשוטים, אך גם <strong>חידות</strong> מתוחכמות.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 6 AND 1 LIMIT 1;

