UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>וגם <strong>לחכם</strong> יועילו הדברים, כי <strong>כשישמעם</strong> - <strong>יוסיף</strong> עוד <strong>ללמוד</strong> מדעתו,&#160; <strong>והנבון יקנה</strong> על-ידם <strong>תחבולות</strong>, והם דברים עמוקים בעיון רב בדקות המחשבה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 5 AND 1 LIMIT 1;

