UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = ' \r\n<p>\r\n<strong>הצדיק</strong> <strong> שונא דברי-שקר</strong>, המעוותים את המציאות ומונעים עשיית צדק;&#160;&#160; אולם <strong>הרשע</strong>  מוכן \r\n<strong>להבאיש ולהחפיר</strong>  את יריבו בדברי-שקר כדי לנצח במשפט.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 13 AND `verse_number` = 5 AND 1 LIMIT 1;

