UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>בראש</strong> מקום שנשמע שם <strong>המיית</strong> בני אדם,&#160;&#160; <strong>בפתחי שערים</strong> - מקום הנכנסים והיוצאים&#160;<strong>בכל עיר ועיר</strong>, <strong>תאמר</strong> כל חכמה את <strong>אמריה</strong> &#160; (והוא ענין מליצה, וכאומר: הלא התורה גלויה לכולם ומזהירה על קיומה).</p>\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>- בכביש <strong>הראשי, ההומה</strong> והרועש ביותר, <strong>קוראת</strong> החכמה;&#160;&#160; <strong>בפתחי שערים</strong>, במקום התכנסות הציבור והמנהיגים, וכן בכל מקום <strong>בתוך העיר</strong>, <strong>אומרת </strong>החכמה את דבריה -</p>', `mqorot` = '<p>\r\n<strong>המיה</strong> היא <a href=\"http://localhost/tnk1/kma/qjrim1/hma.html\">קול גדול, שוטף ומטביע</a>. השורש \r\n<strong>המה</strong>       מופיע בספר משלי במשמעות שלילית, הקשורה לפיתויים <small class=\"small\">(משלי ז11)</small>, כסילות <small class=\"small\">(משלי ט13)</small> ושיכרות<small class=\"small\"> (משלי כ1)</small>. ומכאן:<br />\r\n</p>\r\n     \r\n<p>1. המוֹרוֹת (והמורים) היוצאות לשליחות חינוכית צריכות לפעול גם במקומות כאלה, מקומות <strong>הומים</strong> מכסילות ושיכרות. </p><p>2. ואם הקוראת היא החכמה המופשטת, הפסוק מלמד, שגם במקומות רועשים, <strong>בראש הומיות</strong>, ניתן לשמוע את קולה של החכמה, אם מקשיבים היטב.</p><p>\r\n<strong>שער </strong>במקרא הוא <a href=\"http://localhost/tnk1/kma/qjrim1/jar.html\">מרכז החיים הציבוריים, מקום השלטון והמשפט</a>. הפסוק מלמד, שגם במקומות כאלו, מקומות שבהם מסתובבים אנשים מכובדים ומלאי בטחון עצמי, \r\n<strong>בפתחי שערים בעיר</strong>, גם שם יש להשמיע את קולה של החכמה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 1 AND `verse_number` = 21 AND 1 LIMIT 1;

